import { useState } from "react";
import axios from "axios";
import "../App.css";

export default function BillUpload({ setMessage }) {
  const [supermarket, setSupermarket] = useState("");
  const [amount, setAmount] = useState("");
  const [billFiles, setBillFiles] = useState([]);
  const [useToday, setUseToday] = useState(true);
  const [customDate, setCustomDate] = useState("");
  const [dragOver, setDragOver] = useState(false);

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = () => {
    setDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    const files = Array.from(e.dataTransfer.files);
    const validFiles = files.filter(file => 
      file.type.startsWith('image/') || file.type === 'application/pdf'
    );
    if (validFiles.length > 0) {
      setBillFiles(prev => [...prev, ...validFiles]);
    }
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    const validFiles = files.filter(file => 
      file.type.startsWith('image/') || file.type === 'application/pdf'
    );
    if (validFiles.length > 0) {
      setBillFiles(prev => [...prev, ...validFiles]);
    }
  };
  
  const removeFile = (indexToRemove) => {
    setBillFiles(prev => prev.filter((_, index) => index !== indexToRemove));
  };
  
  const getFileIcon = (file) => {
    if (file.type === 'application/pdf') {
      return '📄';
    } else if (file.type.startsWith('image/')) {
      return '🖼️';
    }
    return '📎';
  };
  
  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append("supermarket", supermarket);
      formData.append("amount", amount);
      formData.append("billImage", billImage);

      if (useToday) {
        formData.append("date", new Date().toISOString());
      } else {
        formData.append("date", new Date(customDate).toISOString());
      }

      // ✅ axios will set the correct Content-Type automatically
      const res = await axios.post(
        "http://127.0.0.1:5000/api/expenses/upload-bill",
        formData
      );

      console.log("Upload response:", res.data);

      // Reset form only if success
      setSupermarket("");
      setAmount("");
      setBillImage(null);
      setCustomDate("");
      setUseToday(true);

      setMessage("✅ Bill uploaded successfully!");
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      console.error("Upload failed:", err);
      setMessage("❌ Bill upload failed. Check server logs.");
      setTimeout(() => setMessage(""), 4000);
    }
  };

  return (
    <div className="card">
      <h2>📸 Upload Bill Receipt</h2>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              placeholder="Shop/Store Name (e.g., Big Bazaar, DMart)"
              value={supermarket}
              onChange={(e) => setSupermarket(e.target.value)}
              required
            />
            <input
              type="number"
              placeholder="Total Amount (₹)"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              min="0"
              step="0.01"
              required
            />
          </div>

          {/* Beautiful Drag & Drop File Upload */}
          <div 
            className={`file-upload-container ${dragOver ? 'dragover' : ''}`}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            onClick={() => document.getElementById('file-input').click()}
          >
            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              alignItems: 'center', 
              gap: '16px'
            }}>
              <div style={{ fontSize: '48px' }}>📷</div>
              {billImage ? (
                <div style={{ textAlign: 'center' }}>
                  <div style={{ 
                    fontSize: '18px', 
                    fontWeight: '600', 
                    color: '#10b981',
                    marginBottom: '8px'
                  }}>
                    ✅ File Selected!
                  </div>
                  <div style={{ 
                    fontSize: '14px', 
                    color: '#6b7280',
                    background: 'rgba(16, 185, 129, 0.1)',
                    padding: '8px 16px',
                    borderRadius: '20px',
                    border: '1px solid rgba(16, 185, 129, 0.3)'
                  }}>
                    {billImage.name}
                  </div>
                  <div style={{ 
                    fontSize: '12px', 
                    color: '#9ca3af', 
                    marginTop: '8px' 
                  }}>
                    Click to change file
                  </div>
                </div>
              ) : (
                <div style={{ textAlign: 'center' }}>
                  <div style={{ 
                    fontSize: '20px', 
                    fontWeight: '600', 
                    color: '#4a5568',
                    marginBottom: '8px'
                  }}>
                    Drop your bill image here
                  </div>
                  <div style={{ 
                    fontSize: '16px', 
                    color: '#718096' 
                  }}>
                    or click to browse files
                  </div>
                  <div style={{ 
                    fontSize: '14px', 
                    color: '#a0aec0', 
                    marginTop: '8px' 
                  }}>
                    Supports: JPG, PNG, WEBP
                  </div>
                </div>
              )}
            </div>
            <input
              id="file-input"
              type="file"
              accept="image/*"
              onChange={handleFileSelect}
              required
              style={{ display: 'none' }}
            />
          </div>

          {/* Date Selection */}
          <div style={{ marginTop: "20px", marginBottom: "20px" }}>
            <div style={{ 
              display: 'flex', 
              gap: '20px', 
              alignItems: 'center',
              flexWrap: 'wrap'
            }}>
              <label style={{
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
                cursor: 'pointer',
                padding: '12px 18px',
                borderRadius: '10px',
                background: useToday ? 'rgba(102, 126, 234, 0.15)' : 'rgba(156, 163, 175, 0.1)',
                border: useToday ? '2px solid #667eea' : '2px solid #d1d5db',
                transition: 'all 0.3s ease',
                fontWeight: '500'
              }}>
                <input
                  type="radio"
                  checked={useToday}
                  onChange={() => setUseToday(true)}
                  style={{ margin: 0, accentColor: '#667eea' }}
                />
                <span>📅 Today ({new Date().toLocaleDateString()})</span>
              </label>
              
              <label style={{
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
                cursor: 'pointer',
                padding: '12px 18px',
                borderRadius: '10px',
                background: !useToday ? 'rgba(102, 126, 234, 0.15)' : 'rgba(156, 163, 175, 0.1)',
                border: !useToday ? '2px solid #667eea' : '2px solid #d1d5db',
                transition: 'all 0.3s ease',
                fontWeight: '500'
              }}>
                <input
                  type="radio"
                  checked={!useToday}
                  onChange={() => setUseToday(false)}
                  style={{ margin: 0, accentColor: '#667eea' }}
                />
                <span>🗓️ Custom Date</span>
              </label>
              
              {!useToday && (
                <input
                  type="date"
                  value={customDate}
                  onChange={(e) => setCustomDate(e.target.value)}
                  required
                  style={{
                    padding: '14px 18px',
                    borderRadius: '10px',
                    border: '2px solid #e2e8f0',
                    fontSize: '16px',
                    fontWeight: '500',
                    background: '#ffffff',
                    transition: 'all 0.3s ease'
                  }}
                />
              )}
            </div>
          </div>

          <button type="submit" className="btn btn-primary">
            🚀 Upload Bill Receipt
          </button>
        </form>
      </div>
    </div>
  );
}
