// src/components/ExpenseList.jsx
import React from "react";
import "../App.css";

function formatDate(value) {
  if (!value) return "—";
  const d = new Date(value);
  if (isNaN(d)) return "—";
  // Format e.g., 01 Oct 2025, 10:30 AM
  return new Intl.DateTimeFormat("en-IN", {
    dateStyle: "medium",
    timeStyle: "short"
  }).format(d);
}

export default function ExpenseList({ expenses }) {
  return (
    <div className="card">
      <h2>📜 Expense List</h2>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Amount</th>
            <th>Category</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {expenses.map((exp, i) => (
            <tr key={i}>
              <td>{exp.title}</td>
              <td className="amount">₹{exp.amount}</td>
              <td>{exp.category}</td>
              <td>{formatDate(exp.date)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
