import { useState } from "react";
import axios from "axios";
import "../App.css";

export default function BillUpload({ setMessage }) {
  const [supermarket, setSupermarket] = useState("");
  const [amount, setAmount] = useState("");
  const [billImage, setBillImage] = useState(null);
  const [useToday, setUseToday] = useState(true);
  const [customDate, setCustomDate] = useState("");
  const [dragOver, setDragOver] = useState(false);

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = () => {
    setDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    const files = e.dataTransfer.files;
    if (files[0] && files[0].type.startsWith('image/')) {
      setBillImage(files[0]);
    }
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setBillImage(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append("supermarket", supermarket);
      formData.append("amount", amount);
      formData.append("billImage", billImage);

      if (useToday) {
        formData.append("date", new Date().toISOString());
      } else {
        formData.append("date", new Date(customDate).toISOString());
      }

      // ✅ axios will set the correct Content-Type automatically
      const res = await axios.post(
        "http://127.0.0.1:5000/api/expenses/upload-bill",
        formData
      );

      console.log("Upload response:", res.data);

      // Reset form only if success
      setSupermarket("");
      setAmount("");
      setBillImage(null);
      setCustomDate("");
      setUseToday(true);

      setMessage("✅ Bill uploaded successfully!");
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      console.error("Upload failed:", err);
      setMessage("❌ Bill upload failed. Check server logs.");
      setTimeout(() => setMessage(""), 4000);
    }
  };

  return (
    <div className="card">
      <h2>📸 Upload Bill</h2>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Shop name"
          value={supermarket}
          onChange={(e) => setSupermarket(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Total Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setBillImage(e.target.files[0])}
          required
        />

        <div style={{ marginTop: "10px" }}>
          <label>
            <input
              type="radio"
              checked={useToday}
              onChange={() => setUseToday(true)}
            />
            Use Today ({new Date().toLocaleDateString()})
          </label>
          <br />
          <label>
            <input
              type="radio"
              checked={!useToday}
              onChange={() => setUseToday(false)}
            />
            Enter Date:
          </label>
          {!useToday && (
            <input
              type="date"
              value={customDate}
              onChange={(e) => setCustomDate(e.target.value)}
              required
            />
          )}
        </div>

        <br />
        <button type="submit">Upload Bill</button>
      </form>
    </div>
  );
}
