import "../App.css";

function formatIST(value) {
  if (!value) return "—";
  const d = new Date(value);
  if (isNaN(d)) return "—";

  return new Intl.DateTimeFormat("en-IN", {
    timeZone: "Asia/Kolkata",
    dateStyle: "medium",
    timeStyle: "short"
  }).format(d);
}

export default function ExpenseList({ expenses = [] }) {
  if (!expenses.length) {
    return (
      <div className="card">
        <h2>📜 Expense List</h2>
        <p>No expenses to show. Use filter to view expenses.</p>
      </div>
    );
  }

  return (
    <div className="card">
      <h2>📜 Expense List</h2>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Amount</th>
            <th>Category</th>
            <th>Date & Time (IST)</th>
          </tr>
        </thead>
        <tbody>
          {expenses.map((exp, i) => (
            <tr key={i}>
              <td>{exp.title}</td>
              <td className="amount">₹{exp.amount}</td>
              <td>{exp.category}</td>
              <td>{formatIST(exp.date)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
