    return (
      <div className="card">
        <h2>ðŸ“œ Expense List</h2>
        <p>No expenses to show. Use filter or add a new one.</p>
      </div>
    );
  }

  return (
    <div className="card">
      <h2>ðŸ“œ Expense List</h2>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Amount</th>
            <th>Category</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {expenses.map((exp, i) => (
            <tr key={i}>
              <td>{exp.title}</td>
              <td className="amount">â‚¹{exp.amount}</td>
              <td>{exp.category}</td>
              <td>{exp.date ? new Date(exp.date).toLocaleDateString() : "â€”"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
