import { useState } from "react";
import axios from "axios";
import "../App.css";

export default function ExpenseFilter({ setExpenses, fetchAllExpenses }) {
  const [date, setDate] = useState("");

  const handleFilter = async () => {
    if (!date) return;
    const res = await axios.get(`http://localhost:5000/api/expenses/${date}`);
    setExpenses(res.data);
  };

  const handleClear = () => {
    setExpenses([]); // clear list
    setDate("");
  };

  return (
    <div className="card">
      <h2>ðŸ”Ž Filter by Date</h2>
      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      <button onClick={handleFilter}>Filter</button>
      <button onClick={handleClear} style={{ marginLeft: "10px", background: "gray" }}>
        Clear
      </button>
    </div>
  );
}
