import { useState } from "react";
import axios from "axios";
import "../App.css";

export default function ExpenseForm({ setMessage }) {
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [useToday, setUseToday] = useState(true); // default: today’s date
  const [customDate, setCustomDate] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Decide which date to use
    let expenseDate;
    if (useToday) {
      expenseDate = new Date().toISOString();
    } else if (customDate) {
      expenseDate = new Date(customDate).toISOString();
    } else {
      expenseDate = new Date().toISOString(); // fallback
    }

    await axios.post("http://localhost:5000/api/expenses", {
      title,
      amount,
      category,
      date: expenseDate
    });

    // Reset form
    setTitle("");
    setAmount("");
    setCategory("");
    setCustomDate("");
    setUseToday(true);

    // Success message only
    setMessage("✅ Expense added successfully!");
    setTimeout(() => setMessage(""), 3000);
  };

  return (
    <div className="card">
      <h2>➕ Add Expense</h2>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <input
          placeholder="Category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        />

        {/* Date Options */}
        <div style={{ marginTop: "10px" }}>
          <label>
            <input
              type="radio"
              checked={useToday}
              onChange={() => setUseToday(true)}
            />
            Use Today’s Date ({new Date().toLocaleDateString()})
          </label>
          <br />
          <label>
            <input
              type="radio"
              checked={!useToday}
              onChange={() => setUseToday(false)}
            />
            Enter Date:
          </label>
          {!useToday && (
            <input
              type="date"
              value={customDate}
              onChange={(e) => setCustomDate(e.target.value)}
              required
            />
          )}
        </div>

        <br />
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
}
