import { useState } from 'react';
import axios from "axios";
import { exportCurrentData } from '../utils/exportUtils';
import ExpenseEditModal from './ExpenseEditModal';
import "../App.css";

function formatDateOnly(value) {
  if (!value) return "‚Äî";
  const d = new Date(value);
  if (isNaN(d)) return "‚Äî";
  return new Intl.DateTimeFormat("en-IN", {
    timeZone: "Asia/Kolkata",
    dateStyle: "medium"
  }).format(d);
}

export default function ExpenseList({ expenses = [], setExpenses, setMessage }) {
  const [editingExpense, setEditingExpense] = useState(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  if (!expenses.length) {
    return (
      <div className="card">
        <h2>üìú Expense List</h2>
        <p>No expenses to show. Use filter to view expenses.</p>
      </div>
    );
  }

  const handleEdit = (expense) => {
    setEditingExpense(expense);
    setIsEditModalOpen(true);
  };

  const handleEditSuccess = (updatedExpense) => {
    // Update the expense in the current list (optimistic update)
    setExpenses(prevExpenses => 
      prevExpenses.map(exp => 
        exp.id === updatedExpense.id ? updatedExpense : exp
      )
    );
    setMessage("‚úèÔ∏è Expense updated successfully!");
    setTimeout(() => setMessage(""), 3000);
  };

  const handleCloseEditModal = () => {
    setIsEditModalOpen(false);
    setEditingExpense(null);
  };

  const handleDelete = async (exp) => {
    try {
      console.log("Delete request for expense:", exp);
      const dateStr = exp.date.split("T")[0]; // extract YYYY-MM-DD
      console.log("Delete URL:", `http://127.0.0.1:5000/api/expenses/${dateStr}/${exp.id}`);

      const response = await axios.delete(`http://127.0.0.1:5000/api/expenses/${dateStr}/${exp.id}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      console.log("Delete response:", response);

      setMessage("üóëÔ∏è Expense deleted successfully!");
      setTimeout(() => setMessage(""), 3000);
      // Remove the deleted expense from current list to maintain filter state
      setExpenses(prevExpenses => prevExpenses.filter(e => e.id !== exp.id));
    } catch (err) {
      console.error("Delete failed - Full error:", err);
      console.error("Error response:", err.response?.data);
      console.error("Error status:", err.response?.status);
      setMessage(`‚ùå Failed to delete expense: ${err.response?.data?.error || err.message}`);
    }
  };

  const total = expenses.reduce((sum, exp) => sum + Number(exp.amount || 0), 0);

  return (
    <div className="card">
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '20px',
        flexWrap: 'wrap',
        gap: '15px'
      }}>
        <h2 style={{ margin: 0 }}>üìú Expense List ({expenses.length} items)</h2>

        {/* Quick Export Buttons */}
        <div style={{ display: 'flex', gap: '10px' }}>
          <button
            onClick={() => exportCurrentData(expenses, 'csv', 'filtered_expenses')}
            style={{
              padding: '10px 16px',
              border: 'none',
              borderRadius: '8px',
              fontSize: '14px',
              fontWeight: '600',
              cursor: 'pointer',
              background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
              color: 'white',
              transition: 'all 0.3s ease',
              boxShadow: '0 2px 8px rgba(16, 185, 129, 0.3)'
            }}
