import "../App.css";

function formatIST(value) {
  if (!value) return "—";
  const d = new Date(value);
  if (isNaN(d)) return "—";

  return new Intl.DateTimeFormat("en-IN", {
    timeZone: "Asia/Kolkata",
    dateStyle: "medium",
    timeStyle: "short"
  }).format(d);
}

export default function ExpenseList({ expenses = [] }) {
  if (!expenses.length) {
    return (
      <div className="card">
        <h2>📜 Expense List</h2>
        <p>No expenses to show. Use filter to view expenses.</p>
      </div>
    );
  }

  // ✅ calculate total for this filtered day
  const total = expenses.reduce((sum, exp) => sum + Number(exp.amount || 0), 0);

  return (
    <div className="card">
      <h2>📜 Expense List</h2>
      <table>
        <thead>
          <tr>
            <th>Type</th>
            <th>Details</th>
            <th>Amount</th>
            <th>Category</th>
            <th>Date (IST)</th>
            <th>Bill Image</th>
          </tr>
        </thead>
        <tbody>
          {expenses.map((exp, i) => (
            <tr key={i}>
              <td>{exp.imageUrl ? "📸 Bill" : "🛒 Grocery"}</td>
              <td>
                {exp.imageUrl
                  ? `Supermarket: ${exp.supermarket}`
                  : `Item: ${exp.title}`}
              </td>
              <td className="amount">₹{exp.amount}</td>
              <td>{exp.category || "—"}</td>
              <td>{formatIST(exp.date)}</td>
              <td>
                {exp.imageUrl ? (
                  <a href={exp.imageUrl} target="_blank" rel="noopener noreferrer">
                    <img
                      src={exp.imageUrl}
                      alt="bill"
                      style={{
                        width: "50px",
                        height: "50px",
                        objectFit: "cover",
                        borderRadius: "6px"
                      }}
                    />
                  </a>
                ) : (
                  "—"
                )}
              </td>
            </tr>
          ))}

          {/* ✅ Total row */}
          <tr style={{ fontWeight: "bold", background: "#f8f8f8" }}>
            <td colSpan="2">TOTAL</td>
            <td className="amount">₹{total}</td>
            <td colSpan="3"></td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}
