import axios from "axios";
import "../App.css";

function formatDateOnly(value) {
  if (!value) return "‚Äî";
  const d = new Date(value);
  if (isNaN(d)) return "‚Äî";
  return new Intl.DateTimeFormat("en-IN", {
    timeZone: "Asia/Kolkata",
    dateStyle: "medium"
  }).format(d);
}

export default function ExpenseList({ expenses = [], setExpenses, setMessage }) {
  if (!expenses.length) {
    return (
      <div className="card">
        <h2>üìú Expense List</h2>
        <p>No expenses to show. Use filter to view expenses.</p>
      </div>
    );
  }

  const handleDelete = async (exp) => {
    try {
      console.log("Delete request for expense:", exp);
      const dateStr = exp.date.split("T")[0]; // extract YYYY-MM-DD
      console.log("Delete URL:", `http://127.0.0.1:5000/api/expenses/${dateStr}/${exp.id}`);

      const response = await axios.delete(`http://127.0.0.1:5000/api/expenses/${dateStr}/${exp.id}`);
      console.log("Delete response:", response);

      setMessage("üóëÔ∏è Expense deleted successfully!");
      setTimeout(() => setMessage(""), 3000);
      // Remove the deleted expense from current list to maintain filter state
      setExpenses(prevExpenses => prevExpenses.filter(e => e.id !== exp.id));
    } catch (err) {
      console.error("Delete failed - Full error:", err);
      console.error("Error response:", err.response?.data);
      console.error("Error status:", err.response?.status);
      setMessage(`‚ùå Failed to delete expense: ${err.response?.data?.error || err.message}`);
    }
  };

  const total = expenses.reduce((sum, exp) => sum + Number(exp.amount || 0), 0);

  return (
    <div className="card">
      <h2>üìú Expense List</h2>
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>üè∑Ô∏è Type</th>
              <th>üìù Details</th>
              <th>üí∞ Amount</th>
              <th>üìÇ Category</th>
              <th>üóìÔ∏è Date</th>
              <th>üñºÔ∏è Image</th>
              <th>‚öôÔ∏è Action</th>
            </tr>
          </thead>
          <tbody>
            {expenses.map((exp, i) => (
              <tr key={exp.id || i}>
                <td>
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    gap: '8px',
                    padding: '8px 12px',
                    borderRadius: '20px',
                    background: (exp.imageUrl || exp.fileUrl) 
                      ? 'linear-gradient(135deg, rgba(255, 193, 7, 0.1) 0%, rgba(255, 235, 59, 0.1) 100%)' 
                      : 'linear-gradient(135deg, rgba(76, 175, 80, 0.1) 0%, rgba(139, 195, 74, 0.1) 100%)',
                    border: (exp.imageUrl || exp.fileUrl) ? '1px solid rgba(255, 193, 7, 0.3)' : '1px solid rgba(76, 175, 80, 0.3)',
                    fontWeight: '600',
                    fontSize: '14px'
                  }}>
                    {(exp.imageUrl || exp.fileUrl) ? 
                      (exp.isPDF ? "üìÑ Bill PDF" : "üì∏ Bill") : 
                      "üõçÔ∏è Grocery"}
                  </div>
                </td>
                <td style={{ fontWeight: '500' }}>
                  {exp.imageUrl ? (
                    <div>
                      <div style={{ fontWeight: '600', color: '#4a5568' }}>Supermarket</div>
                      <div style={{ color: '#718096', fontSize: '14px' }}>{exp.supermarket}</div>
                    </div>
                  ) : (
                    <div>
                      <div style={{ fontWeight: '600', color: '#4a5568' }}>Item</div>
                      <div style={{ color: '#718096', fontSize: '14px' }}>{exp.title}</div>
                    </div>
                  )}
                </td>
                <td>
                  <div className="amount" style={{ 
                    fontSize: '18px',
                    fontWeight: '700',
                    background: 'linear-gradient(135deg, rgba(16, 185, 129, 0.1) 0%, rgba(5, 150, 105, 0.1) 100%)',
                    padding: '8px 16px',
                    borderRadius: '12px',
                    border: '1px solid rgba(16, 185, 129, 0.3)',
                    display: 'inline-block'
                  }}>
                    ‚Çπ{exp.amount}
                  </div>
                </td>
                <td>
                  <div style={{
                    padding: '6px 12px',
                    borderRadius: '16px',
                    background: 'rgba(102, 126, 234, 0.1)',
                    border: '1px solid rgba(102, 126, 234, 0.3)',
                    color: '#4c51bf',
                    fontSize: '14px',
                    fontWeight: '500',
                    display: 'inline-block'
                  }}>
                    {exp.category || "üìÜ Uncategorized"}
                  </div>
                </td>
                <td style={{ 
                  fontWeight: '500',
                  color: '#4a5568'
                }}>
                  {formatDateOnly(exp.date)}
                </td>
                <td>
                  {exp.imageUrl ? (
                    <a href={exp.imageUrl} target="_blank" rel="noopener noreferrer">
                      <img
                        src={exp.imageUrl}
                        alt="bill"
                        className="bill-image"
                        style={{ 
                          width: "60px", 
                          height: "60px", 
                          objectFit: "cover", 
                          borderRadius: "12px",
                          border: '2px solid #e2e8f0',
                          cursor: 'pointer'
                        }}
                      />
                    </a>
                  ) : (
                    <div style={{
                      width: '60px',
                      height: '60px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      background: 'rgba(156, 163, 175, 0.1)',
                      borderRadius: '12px',
                      border: '2px dashed #cbd5e0',
                      color: '#9ca3af',
                      fontSize: '24px'
                    }}>
                      üñºÔ∏è
                    </div>
                  )}
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleDelete(exp)}
                    title="Delete this expense"
                  >
                    üóëÔ∏è Delete
                  </button>
                </td>
              </tr>
            ))}

            <tr style={{ 
              background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%)',
              borderTop: '3px solid #667eea'
            }}>
              <td colSpan="2" style={{ 
                fontWeight: 'bold',
                fontSize: '18px',
                color: '#4a5568',
                padding: '20px 15px'
              }}>
                üìä TOTAL EXPENSES
              </td>
              <td style={{ padding: '20px 15px' }}>
                <div className="amount" style={{ 
                  fontSize: '24px',
                  fontWeight: '800',
                  background: 'linear-gradient(135deg, rgba(16, 185, 129, 0.2) 0%, rgba(5, 150, 105, 0.2) 100%)',
                  padding: '12px 20px',
                  borderRadius: '16px',
                  border: '2px solid rgba(16, 185, 129, 0.4)',
                  display: 'inline-block',
                  boxShadow: '0 4px 12px rgba(16, 185, 129, 0.2)'
                }}>
                  ‚Çπ{total}
                </div>
              </td>
              <td colSpan="4" style={{ padding: '20px 15px' }}></td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}
