import { useState } from "react";
import axios from "axios";
import "../App.css";

export default function GroceryForm({ setMessage }) {
  const [itemName, setItemName] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [useToday, setUseToday] = useState(true);
  const [customDate, setCustomDate] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    let expenseDate;
    if (useToday) {
      expenseDate = new Date().toISOString();
    } else if (customDate) {
      expenseDate = new Date(customDate).toISOString();
    } else {
      expenseDate = new Date().toISOString();
    }

    await axios.post("http://localhost:5000/api/expenses", {
      title: itemName,
      amount,
      category,
      date: expenseDate,
    });

    setItemName("");
    setAmount("");
    setCategory("");
    setCustomDate("");
    setUseToday(true);

    setMessage("âœ… Grocery expense added successfully!");
    setTimeout(() => setMessage(""), 3000);
  };

  return (
    <div className="card">
      <h2>ðŸ›’ Add Grocery Expense</h2>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Item Name"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <input
          placeholder="Category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          required
        />

        <div style={{ marginTop: "10px" }}>
          <label>
            <input
              type="radio"
              checked={useToday}
              onChange={() => setUseToday(true)}
            />
            Use Todayâ€™s Date ({new Date().toLocaleDateString()})
          </label>
          <br />
          <label>
            <input
              type="radio"
              checked={!useToday}
              onChange={() => setUseToday(false)}
            />
            Enter Date:
          </label>
          {!useToday && (
            <input
              type="date"
              value={customDate}
              onChange={(e) => setCustomDate(e.target.value)}
              required
            />
          )}
        </div>

        <br />
        <button type="submit">Add Grocery</button>
      </form>
    </div>
  );
}
