import { useState } from "react";
import { AuthProvider, useAuth } from "./contexts/AuthContext";
import Login from "./components/Login";
import Register from "./components/Register";
import ExpenseForm from "./components/ExpenseForm";
import ExpenseList from "./components/ExpenseList";
import ExpenseFilter from "./components/ExpenseFilter";
import BillUpload from "./components/BillUpload";
import "./App.css";

// Main authenticated app component
function ExpenseApp() {
  const [expenses, setExpenses] = useState([]);
  const [message, setMessage] = useState("");
  const { user, logout } = useAuth();

  return (
    <div className="container">
      {/* User Header */}
      <div className="user-header">
        <div className="user-info">
          <div>
            <h3>👤 Welcome, {user?.name || 'User'}!</h3>
            <p>📧 {user?.email}</p>
          </div>
        </div>
        <button onClick={logout} className="logout-btn">
          🚪 Logout
        </button>
      </div>

      <h1>💰 Expense Tracker</h1>

      {/* ✅ Display success/error messages */}
      {message && (
        <div className={`message ${message.startsWith("❌") ? "error" : "success"}`}>
          {message}
        </div>
      )}

      {/* Forms */}
      <ExpenseForm setMessage={setMessage} />
      <BillUpload setMessage={setMessage} />
      <ExpenseFilter setExpenses={setExpenses} />

      {/* Pass setExpenses + setMessage to list so delete works */}
      <ExpenseList
        expenses={expenses}
        setExpenses={setExpenses}
        setMessage={setMessage}
      />
    </div>
  );
}

// Auth wrapper component
function AuthWrapper() {
  const { isAuthenticated, loading } = useAuth();
  const [showRegister, setShowRegister] = useState(false);

  if (loading) {
    return (
      <div className="auth-container">
        <div className="auth-card">
          <h1>💰 Expense Tracker</h1>
          <p style={{ textAlign: 'center', fontSize: '18px' }}>🔄 Loading...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return showRegister ? (
      <Register onSwitchToLogin={() => setShowRegister(false)} />
    ) : (
      <Login onSwitchToRegister={() => setShowRegister(true)} />
    );
  }

  return <ExpenseApp />;
}

function App() {
  return (
    <AuthProvider>
      <AuthWrapper />
    </AuthProvider>
  );
}

export default App;
