import { useState } from "react";
import axios from "axios";
import "../App.css";

export default function BillUpload({ setMessage }) {
  const [supermarket, setSupermarket] = useState("");
  const [amount, setAmount] = useState("");
  const [billImage, setBillImage] = useState(null);
  const [useToday, setUseToday] = useState(true);
  const [customDate, setCustomDate] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("supermarket", supermarket);
    formData.append("amount", amount);
    formData.append("billImage", billImage);

    // Decide which date to use
    if (useToday) {
      formData.append("date", new Date().toISOString());
    } else if (customDate) {
      formData.append("date", new Date(customDate).toISOString());
    } else {
      formData.append("date", new Date().toISOString());
    }

    try {
      await axios.post("http://localhost:5000/api/expenses/upload-bill", formData);
      setSupermarket("");
      setAmount("");
      setBillImage(null);
      setCustomDate("");
      setUseToday(true);

      setMessage("‚úÖ Bill uploaded successfully!");
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      console.error("Upload error:", err.message);
      setMessage("‚ùå Failed to upload bill. Check backend logs.");
    }
  };

  return (
    <div className="card">
      <h2>üì∏ Upload Bill</h2>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Supermarket"
          value={supermarket}
          onChange={(e) => setSupermarket(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Total Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setBillImage(e.target.files[0])}
          required
        />

        <div style={{ marginTop: "10px" }}>
          <label>
            <input type="radio" checked={useToday} onChange={() => setUseToday(true)} />
            Use Today‚Äôs Date ({new Date().toLocaleDateString()})
          </label>
          <br />
          <label>
            <input type="radio" checked={!useToday} onChange={() => setUseToday(false)} />
            Enter Date:
          </label>
          {!useToday && (
            <input
              type="date"
              value={customDate}
              onChange={(e) => setCustomDate(e.target.value)}
              required
            />
          )}
        </div>

        <br />
        <button type="submit">Upload Bill</button>
      </form>
    </div>
  );
}
