import { useState, useEffect } from "react";
import axios from "axios";
import ExpenseForm from "./components/ExpenseForm";
import ExpenseList from "./components/ExpenseList";
import ExpenseFilter from "./components/ExpenseFilter";
import BillUpload from "./components/BillUpload";
import "./App.css";

function App() {
  const [expenses, setExpenses] = useState([]);
  const [message, setMessage] = useState("");

  // ✅ Fetch all expenses from backend
  const fetchExpenses = async () => {
    try {
      const res = await axios.get("http://127.0.0.1:5000/api/expenses");
      setExpenses(res.data);
    } catch (err) {
      console.error("Failed to fetch expenses:", err);
      setMessage("❌ Failed to fetch expenses.");
    }
  };

  // ✅ Load data once on mount
  useEffect(() => {
    fetchExpenses();
  }, []);

  return (
    <div className="container">
      <h1>💰 Expense Tracker</h1>

      {/* ✅ Display success/error messages */}
      {message && (
        <div className={`message ${message.startsWith("❌") ? "error" : "success"}`}>
          {message}
        </div>
      )}

      {/* Forms */}
      <ExpenseForm setMessage={setMessage} />
      <BillUpload setMessage={setMessage} />
      <ExpenseFilter setExpenses={setExpenses} />

      {/* Pass fetchExpenses + setMessage to list so delete works */}
      <ExpenseList
        expenses={expenses}
        fetchExpenses={fetchExpenses}
        setMessage={setMessage}
      />
    </div>
  );
}

export default App;
